mapper.select:
SELECT A.i_board, A.seq, A.title, A.r_dt, A.hits , B.i_user, B.nm AS writer_nm, B.profile_img , ifnull(C.favorite_cnt, 0) AS favorite_cnt FROM t_board A LEFT JOIN t_user B ON A.i_user = B.i_user LEFT JOIN ( SELECT i_board, COUNT(i_board) AS favorite_cnt FROM t_board_favorite GROUP BY i_board ) C ON A.i_board = C.i_board WHERE A.typ = #{typ}
AND title LIKE CONCAT('%', #{searchText}, '%')
AND A.ctnt LIKE '%${searchText}%'
AND (A.title LIKE '%${searchText}%' OR A.ctnt LIKE '%${searchText}%')
AND B.nm LIKE '%${searchText}%'
ORDER BY seq DESC LIMIT #{sIdx}, #{recordCntPerPage}

SELECT A.i_board, A.typ, A.seq, A.title, A.ctnt, A.r_dt, A.hits , B.i_user, B.nm AS writer_nm, B.profile_img , ifnull(C.favorite_cnt, 0) AS favorite_cnt , CASE WHEN D.i_board IS NULL THEN 0 ELSE 1 END AS is_favorite
FROM t_board A LEFT JOIN t_user B ON A.i_user = B.i_user
LEFT JOIN ( SELECT i_board, COUNT(i_board) AS favorite_cnt FROM t_board_favorite GROUP BY i_board ) C ON A.i_board = C.i_board
LEFT JOIN t_board_favorite D ON A.i_board = D.i_board AND D.i_user = #{i_user}
WHERE A.i_board = #{i_board}

mapper.update:
UPDATE t_board
SET title = #{title}
, ctnt = #{ctnt}
WHERE i_board = #{i_board}
AND i_user = #{i_user}

UPDATE t_board
SET hits = hits + 1
WHERE i_board = #{i_board}

mapper.delete:
DELETE FROM t_board
WHERE i_board = #{i_board}
AND i_user = #{i_user}

mapper.update:
UPDATE t_board_cmt
SET ctnt = #{ctnt}
WHERE i_cmt = #{i_cmt} AND i_user = #{i_user}
